import {useState} from 'react';
import axios from 'axios'


const Login = ()=> {
    const [username , setUserName] = useState("");

    const handleLoadingUsername = (event)=> {
        setUserName(event.target.value);
    }

    const handleLogin = async ()=> {
    
        const details = {username};
        const promise = await axios.post("http://localhost:5001/login" , JSON.stringify(details));
        const actualData = await promise.json();
        console.log(actualData);
        setUserName("");
    }

    return <div>
        <input type = "text" value = {username} onChange = {handleLoadingUsername} />
        <button type = "button" onClick  = {handleLogin}>login</button>
    </div>
}

export default Login;
